from BehaviorStates.BaseState import BaseStateimport timeclass IdleState(BaseState):    def __init__(self):        super(IdleState, self).__init__()        self.timer = 2    def onEnter(self):        super(IdleState, self).onEnter()        self.timer = 2        self.sensorBridge.send('a')    def onLeave(self):        super(IdleState, self).onLeave()    def onUpdate(self, delta):        super(IdleState, self).onUpdate(delta)        self.timer -= delta        self.sensorBridge.send("team1")        time.sleep(1)        self.sensorBridge.send("team2")        time.sleep(1)        self.sensorBridge.send("team3")        time.sleep(1)